INSERT INTO `ShopItems` (`id`, `itemName`, `group`, `action`, `amount`, `minAmount`, `description`, `shown`) VALUES ('75990b7b-d39c-11e0-8306-001cc494be80', 'Штукатурка  МР-75 МН 30кг Кнауф', '7bf7405b-99a5-11db-937f-000e0c431b59', '0', '229', '', 'Высокоадгезионная, универсальная сухая штукатурная смесь на основе гипса с полимерными добавками. Гипсовые сухие смеси могут быть различного цвета, от белого до серого и даже до розового. Это объясняется наличием природных примесей в гипсовом камне. Цвет смеси никак не влияет на ее характеристики.', '1');
INSERT INTO `ShopItems` (`id`, `itemName`, `group`, `action`, `amount`, `minAmount`, `description`, `shown`) VALUES ('7f6fe6a2-1482-11df-860d-000e0c431b58', 'Штукатурка  гипсовая Т-25 ГИПСВЭЛЛ 10кг  ОСНОВИТ ', '07e40b11-53f8-11de-a649-000e0c431b58', '0', '6', '267', 'Гипсовая штукатурка серая ОСНОВИТ ГИПСВЭЛЛ Т-25 предназначена для выравнивания стен и потолков в помещениях с нормальной влажностью. Для внутренних работ.', '1');
INSERT INTO `ShopItems` (`id`, `itemName`, `group`, `action`, `amount`, `minAmount`, `description`, `shown`) VALUES ('8f0c8f4f-99aa-11db-937f-000e0c431b59', 'Мастика ФЛЕХЕНДИХТ 5кг  Кнауф', '7bf74057-99a5-11db-937f-000e0c431b59', '0', '4', '7', 'КНАУФ-Флэхендихт, готовый состав, представляет собой не содержащую растворитель каучуко-битумную эмульсию, имеет хорошее сцепление почти со всеми основаниями, например с бетоном, известковой, цементной и гипсовой штукатуркой, гипсокартонными КНАУФ-листами и гипсоволокнистыми КНАУФ-суперлистами, кирпичной и каменной кладкой, асбестоцементом, деревом, древесно-стружечными и древесноволокнистыми плитами, полиуретаном, пенопластом, плиточной облицовкой, металлом. КНАУФ-Флэхендихт , безопасна для здоровья, не имеет запаха и вкуса.', '1');
INSERT INTO `ShopItems` (`id`, `itemName`, `group`, `action`, `amount`, `minAmount`, `description`, `shown`) VALUES ('8f0c8f51-99aa-11db-937f-000e0c431b59', 'Монтажный клей гипсовый  Перлфикс  30кг  Кнауф', '7bf74057-99a5-11db-937f-000e0c431b59', '0', '249', '113', 'Сухая монтажная смесь на основе гипса с полимерными добавками, обеспечивающими повышенную адгезию. Гипсовые сухие смеси могут быть различного цвета, от белого до серого и даже до розового. Это объясняется наличием природных примесей в гипсовом камне. Цвет смеси никак не влияет на ее характеристики.', '1');
INSERT INTO `ShopItems` (`id`, `itemName`, `group`, `action`, `amount`, `minAmount`, `description`, `shown`) VALUES ('8f0c8f59-99aa-11db-937f-000e0c431b59', 'Плиточный клей Флизенклебер 10 кг  Кнауф', '7bf74059-99a5-11db-937f-000e0c431b59', '0', '290', '134', 'Плиточный клей КНАУФ - универсальная сухая смесь на цементной основе с повышенной эластичностью и адгезией. Используется для внутренних и наружных работ и предназначен для облицовки стен и пола керамической плиткой и природным камнем, а также для крепления изоляционных материалов,(плит из пенополистирола, минеральной ваты, пенополиуретана и т.п.). Особенно рекомендуется для облицовки поверхностей с повышенными нагрузками (полы производственных помещений), поверхностей, подверженных колебаниям температуры (террасы, балконы, отапливаемые полы), а также для применения на деформируемых основаниях (ДСП, ЦСП) и при облицовке плиткой по плитке. После высыхания тепло-, морозо- и водоустойчив.', '1');
